import { createSlice } from "@reduxjs/toolkit";

const counterSlice = createSlice({
  name: "counter",
  initialState: { counter: 0, showCounter: false },
  reducers: {
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload;
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});




// Actions objects are automatically created behind the scenes.
// Just need to put it in the actions like counterSlice.actions.increment is equal to {type: "increment"}
// We need to export it like a whole object and then we can pass it in dispatch.
// Actions that are passed in dispatch need to be passed as functions so it can automatically pass to the  respective reducer function.
export const counterActions = counterSlice.actions;
export default counterSlice.reducer;
